import Vue from 'vue';
import Router from 'vue-router';
import HomePage from '@/components/HomePage'; // 主页组件
import QuestionsList from '@/components/QuestionsList'; // 问题列表组件
import QuestionForm from '@/components/QuestionForm'; // 添加/编辑问题的表单组件

Vue.use(Router);

export default new Router({
  mode: 'history',
  routes: [
    {
      path: '/',
      name: 'HomePage',
      component: HomePage,
    },
    {
      path: '/questions',
      name: 'QuestionsList',
      component: QuestionsList,
    },
    {
      path: '/question/add',
      name: 'AddQuestion',
      component: QuestionForm, // 用于添加问题
    },
    {
      path: '/question/edit/:id', // :id 是动态参数
      name: 'EditQuestion',
      component: QuestionForm, // 用于编辑问题
      props: true,
    },
  ],
});
